import React, { Component } from 'react';
import Die from './Die';
import './RollDice.css';

const diceNumArray = ['one', 'two', 'three', 'four', 'five', 'six']

function randomDie() {
        return diceNumArray[Math.floor(Math.random() * diceNumArray.length)];
    }

class RollDice extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isButtonDisabled: false,
            die1: randomDie(), 
            die2: randomDie(),
            die3: randomDie(), 
            die4: randomDie(),
            die5: randomDie(), 
            die6: randomDie(),
        }
        this.rollButton = this.rollButton.bind(this)
    }

    rollButton() {
        //disable button & apply CSS for shake 
        this.setState({isButtonDisabled:true});

        //change die faces 
        setTimeout(function(){
            this.setState({ 
                die1: randomDie(),
                die2: randomDie(),
            })
        }.bind(this), 100)

        //enable the roll button on a timed delay
        setTimeout(function() {
            this.setState({isButtonDisabled:false})}.bind(this), 1000);

    }

    render() {
        return (
            <div>
                <div className="RollDice-wrapper">
                    <Die className='RollDice-shake' num={this.state.die1} shake={this.state.isButtonDisabled}/>
                    <Die className='RollDice-shake' num={this.state.die2} shake={this.state.isButtonDisabled}/>
                </div>
                <div>
                    <button disabled={this.state.isButtonDisabled} onClick={this.rollButton} className="RollDice-button">{this.state.isButtonDisabled === true ? 'Rolling...' : 'Roll Dice!'}</button>
                </div>              
            </div>
        )
    }
}

export default RollDice;
