import React, { Component } from 'react';
import Die from './Die';
import './RollDice.css';

const diceNumArray = ['one', 'two', 'three', 'four', 'five', 'six']

function random() {
        return diceNumArray[Math.floor(Math.random() * diceNumArray.length)];
    }



class RollDice extends Component {
    constructor(props) {
        super(props);
        this.state = {
            die1: 'one', 
            die2: 'two',
        }
        this.rollButton = this.rollButton.bind(this)
    }

    rollButton(e) {
        this.setState({ 
            die1: random(),
            die2: random(),
        });
    }

    render() {
        return (
            <div>
                <div className="RollDice-wrapper">
                    <Die num={this.state.die1}/>
                    <Die num={this.state.die2}/>
                </div>
                <div>
                    <button onClick={this.rollButton} className="RollDice-button">Roll Dice!</button>
                </div>              
            </div>
        )
    }
}

export default RollDice;
