import React, { Component } from 'react';
import Die from './Die';

const diceNumArray = ['one', 'two', 'three', 'four', 'five', 'six']

class RollDice extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dieNumberFace: 'one', 
        }
    }

    rollButton(e) {
        let newDieNum = diceNumArray[Math.floor(Math.random() * diceNumArray.length)]
        console.log(newDieNum)
        this.setState({dieNumberFace: newDieNum});
    }

    render() {
        return (
            <div>
                <div className="RollDice-wrapper">
                    <Die num={this.state.dieNumberFace}/>
                    <Die num={diceNumArray[2]}/>
                </div>
                <div>
                    <button onClick={this.rollButton} className="RollDice-button">Roll Dice!</button>
                </div>              
            </div>
        )
    }
}

export default RollDice;
