import React, { Component } from 'react';
import Die from './Die';
import './RollDice.css';

const diceNumArray = ['one', 'two', 'three', 'four', 'five', 'six']

function randomDie() {
        return diceNumArray[Math.floor(Math.random() * diceNumArray.length)];
    }

class RollDice extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isButtonDisabled: false,
            die1: randomDie(), 
            die2: randomDie(),
        }
        this.rollButton = this.rollButton.bind(this)
    }

    rollButton(e) {
        this.setState({ 
            die1: randomDie(),
            die2: randomDie(),
        });
    }

    render() {
        return (
            <div>
                <div className="RollDice-wrapper">
                    <Die className='RollDice-shake' num={this.state.die1}/>
                    <Die className='RollDice-shake' num={this.state.die2}/>
                </div>
                <div>
                    <button disabled={this.isButtonDisabled} onClick={this.rollButton} className="RollDice-button">{this.isButtonDisabled = false ? 'Rolling...' : 'Roll Dice!'}</button>
                </div>              
            </div>
        )
    }
}

export default RollDice;
